 import React from 'react'; 
import ProductCard from './ProductCard';
import nastyImg from '../assets/nasty.png';


const ProductsSection = () => {
  const products = [
    {
      id: 1,
      name: 'Nasty Juice Sal 30ML Wicked Haze',
      description: 'Sabores: Grosella negra y limonada . Tamaño: 30ML',
      price: 299,
      image: nastyImg
    },
    {
      id: 2,
      name: 'Pod Recargable',
      description: 'Batería de larga duración',
      price: 499,
      image: 'https://via.placeholder.com/300'
    },
    {
      id: 3,
      name: 'Kit de Inicio',
      description: 'Todo lo necesario para comenzar',
      price: 899,
      image: 'https://via.placeholder.com/300'
    }
  ];

  return (
    <section id="products" className="py-16 px-6 bg-gray-100">
      <div className="container mx-auto">
        <h2 className="text-3xl font-bold mb-12 text-center">Nuestros Productos</h2>

        <div className="mb-8 flex flex-wrap justify-center gap-4">
          <button className="bg-black text-white px-4 py-2 rounded-full">Todos</button>
          <button className="bg-gray-200 hover:bg-gray-300 px-4 py-2 rounded-full transition-colors">Descartables</button>
          <button className="bg-gray-200 hover:bg-gray-300 px-4 py-2 rounded-full transition-colors">Recargables</button>
          <button className="bg-gray-200 hover:bg-gray-300 px-4 py-2 rounded-full transition-colors">Kits</button>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {products.map(product => (
            <ProductCard key={product.id} product={product} />
          ))}
        </div>
      </div>
    </section>
  );
};

export default ProductsSection;
