import React, { useState } from 'react';
import LayoutHeader from './components/LayoutHeader';
import HeroSection from './components/HeroSection';
import ProductsSection from './components/ProductsSection';
import AboutSection from './components/AboutSection';
import ContactSection from './components/ContactSection';
import Footer from './components/Footer';
import LocationMap from './components/LocationMap';
import ContactInfo from './components/ContactInfo'; // Asegúrate de tener este componente

const App = () => {
  const [currentPage] = useState('home');

  return (
    <div className="min-h-screen flex flex-col">
      <LayoutHeader />

      <main className="flex-grow">
        {currentPage === 'home' && (
          <>
            <HeroSection />
            <ProductsSection />
            <AboutSection />
            <ContactSection />

            {/* Agrega esta sección donde quieras que aparezca */}
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12 grid grid-cols-1 lg:grid-cols-3 gap-8">
              <div className="lg:col-span-1">
                <ContactInfo />
              </div>
              <div className="lg:col-span-2">
                <LocationMap />
              </div>
            </div>
          </>
        )}
      </main>

      <Footer />
    </div>
  );
};

export default App;
