import React, { useEffect, useRef } from 'react';
import ProductCard from './ProductCard';
import nastyImg from '../assets/nasty.png';
import nastImg from '../assets/nastyamarillo.png';
import mangoImg from '../assets/mangp.png';

const ProductsSection = () => {
  const scrollRef = useRef(null);

  const products = [
    {
      id: 1,
      name: 'Nasty Juice Sal 30ML Wicked Haze',
      description: 'Sabores: Grosella negra y limonada ',
      price: 80,
      image: nastyImg
    },
    {
      id: 2,
      name: 'Nasty Juice Devil Teeth Salt 30ml Recargable',
      description: 'Sabor Melon',
      price: 80,
      image: nastImg
    },
    {
      id: 3,
      name: 'Nasty Juice Mango Cushman 30ML',
      description: 'Sabor Mango ',
      price: 80,
      image: mangoImg
    }
    // Puedes agregar más productos aquí
  ];

  useEffect(() => {
    const scrollContainer = scrollRef.current;
    const interval = setInterval(() => {
      if (scrollContainer) {
        scrollContainer.scrollBy({
          left: 300, // ajusta la distancia según el tamaño de la tarjeta
          behavior: 'smooth',
        });

        // Si llegó al final, vuelve al inicio
        if (
          scrollContainer.scrollLeft + scrollContainer.offsetWidth >=
          scrollContainer.scrollWidth
        ) {
          scrollContainer.scrollTo({ left: 0, behavior: 'smooth' });
        }
      }
    }, 3000); // cambia cada 3 segundos

    return () => clearInterval(interval);
  }, []);

  return (
    <section id="products" className="py-16 px-6 bg-gray-100">
      <div className="container mx-auto">
        <h2 className="text-3xl font-bold mb-12 text-center">Nuestros Productos</h2>

        <div className="mb-8 flex flex-wrap justify-center gap-4">
          <button className="bg-black text-white px-4 py-2 rounded-full">Todos</button>
          <button className="bg-gray-200 hover:bg-gray-300 px-4 py-2 rounded-full transition-colors">Descartables</button>
          <button className="bg-gray-200 hover:bg-gray-300 px-4 py-2 rounded-full transition-colors">Recargables</button>
          <button className="bg-gray-200 hover:bg-gray-300 px-4 py-2 rounded-full transition-colors">Kits</button>
        </div>

        {/* Carrusel automático + scroll manual */}
        <div
          ref={scrollRef}
          className="flex overflow-x-auto space-x-6 snap-x snap-mandatory scrollbar-hide px-2"
        >
          {products.map(product => (
            <div key={product.id} className="snap-start shrink-0 w-72">
              <ProductCard product={product} />
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default ProductsSection;
