import React, { useState, useEffect } from 'react';

// Aquí debes importar tus imágenes, ejemplo:
// import nastyImg from 'ruta/a/nastyImg.jpg';
// import nastImg from 'ruta/a/nastImg.jpg';
// ... etc

const products = [
  {
    id: 1,
    name: 'Nasty Juice Sal 30ML Wicked Haze',
    description: 'Sabores: Grosella negra y limonada',
    price: 80,
    image: nastyImg,
    category: 'salt',
  },
  {
    id: 2,
    name: 'Nasty Juice Devil Teeth Salt 30ml Recargable',
    description: 'Sabor Melón',
    price: 80,
    image: nastImg,
    category: 'salt',
  },
  // ... todos tus productos (como ya los tienes)
  {
    id: 16,
    name: 'Vincci Royal',
    description: 'Tanque de 50ml',
    price: 80,
    image: '',
    category: 'recargables',
  },
  // y así hasta el final
];

const HeroSection = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  // Cambia el producto cada 5 segundos
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % products.length);
    }, 5000);

    // Cleanup para cuando el componente se desmonta
    return () => clearInterval(interval);
  }, []);

  const product = products[currentIndex];

  return (
    <section id="home" className="relative bg-gradient-to-r from-black to-gray-900 text-white py-20 px-6">
      <div className="container mx-auto flex flex-col md:flex-row items-center">
        <div className="md:w-1/2 mb-10 md:mb-0">
          <h1 className="text-4xl md:text-5xl font-bold mb-4">Energiza tu experiencia de vapeo</h1>
          <p className="text-lg mb-8">Descubre los mejores productos premium para una experiencia única</p>
          <button className="bg-red-600 hover:bg-red-700 text-white font-bold py-3 px-8 rounded-full transition-colors shadow-lg">
            Ver productos
          </button>
        </div>
        <div className="md:w-1/2 flex justify-center">
          <div className="relative w-64 h-64 md:w-80 md:h-80">
            <div className="absolute inset-0 bg-blue-500 rounded-full opacity-20 blur-xl"></div>
            <div className="relative bg-gray-800 rounded-2xl p-6 shadow-2xl flex flex-col items-center justify-center">
              {product.image ? (
                <img
                  src={product.image}
                  alt={product.name}
                  className="w-full h-auto rounded-lg mb-4 max-h-48 object-contain"
                />
              ) : (
                <div className="w-full h-48 bg-gray-700 rounded-lg mb-4 flex items-center justify-center text-gray-400">
                  Sin imagen
                </div>
              )}
              <h2 className="text-xl font-semibold text-center">{product.name}</h2>
              <p className="text-sm text-gray-300 text-center">{product.description}</p>
              <p className="mt-2 text-red-500 font-bold">${product.price}</p>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};
